import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploadStatus, setUploadStatus] = useState('');
  const [stats, setStats] = useState(null);

  // ÏãúÏä§ÌÖú ÌÜµÍ≥Ñ Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/stats');
      setStats(response.data);
    } catch (error) {
      console.error('ÌÜµÍ≥Ñ Ï°∞Ìöå Ïã§Ìå®:', error);
    }
  };

  useEffect(() => {
    fetchStats();
  }, []);

  // ÌååÏùº ÏóÖÎ°úÎìú Ï≤òÎ¶¨
  const handleFileUpload = async () => {
    if (!file) {
      alert('ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    setUploadStatus('ÏóÖÎ°úÎìú Ï§ë...');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setUploadStatus(`‚úÖ ${response.data.message} (Ï≤≠ÌÅ¨: ${response.data.chunks_created}Í∞ú)`);
      fetchStats(); // ÌÜµÍ≥Ñ ÏóÖÎç∞Ïù¥Ìä∏
    } catch (error) {
      setUploadStatus(`‚ùå ÏóÖÎ°úÎìú Ïã§Ìå®: ${error.response?.data?.error || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // ÏßàÎ¨∏ Ï≤òÎ¶¨
  const handleQuestion = async () => {
    if (!question.trim()) {
      alert('ÏßàÎ¨∏ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setLoading(true);
    setAnswer('ÎãµÎ≥ÄÏùÑ ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§...');

    try {
      const response = await axios.post('/api/query', {
        question: question.trim(),
      });

      setAnswer(response.data.answer);
    } catch (error) {
      setAnswer(`‚ùå Ïò§Î•ò: ${error.response?.data?.error || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // DB Ï¥àÍ∏∞Ìôî
  const handleClearDB = async () => {
    if (!window.confirm('Ï†ïÎßêÎ°ú Î≤°ÌÑ∞ DBÎ•º Ï¥àÍ∏∞ÌôîÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      return;
    }

    try {
      await axios.post('/api/clear');
      alert('Î≤°ÌÑ∞ DBÍ∞Ä Ï¥àÍ∏∞ÌôîÎêòÏóàÏäµÎãàÎã§.');
      fetchStats(); // ÌÜµÍ≥Ñ ÏóÖÎç∞Ïù¥Ìä∏
    } catch (error) {
      alert(`Ï¥àÍ∏∞Ìôî Ïã§Ìå®: ${error.response?.data?.error || error.message}`);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ü§ñ RAG ÏãúÏä§ÌÖú</h1>
        <p>Î¨∏ÏÑú Í∏∞Î∞ò ÏßàÏùòÏùëÎãµ ÏãúÏä§ÌÖú</p>
      </header>

      <main className="App-main">
        {/* ÏãúÏä§ÌÖú ÌÜµÍ≥Ñ */}
        {stats && (
          <div className="stats-section">
            <h3>üìä ÏãúÏä§ÌÖú ÌÜµÍ≥Ñ</h3>
            <div className="stats-grid">
              <div className="stat-item">
                <span className="stat-label">Ï¥ù Î¨∏ÏÑú Ïàò:</span>
                <span className="stat-value">{stats.vector_store?.total_documents || 0}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Ïù∏Îç±Ïä§ ÌÅ¨Í∏∞:</span>
                <span className="stat-value">{stats.vector_store?.index_size || 0}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Ï≤≠ÌÅ¨ ÌÅ¨Í∏∞:</span>
                <span className="stat-value">{stats.chunk_size || 500}</span>
              </div>
            </div>
          </div>
        )}

        {/* Î¨∏ÏÑú ÏóÖÎ°úÎìú ÏÑπÏÖò */}
        <section className="upload-section">
          <h3>üìÑ Î¨∏ÏÑú ÏóÖÎ°úÎìú</h3>
          <div className="upload-container">
            <input
              type="file"
              accept=".pdf,.txt"
              onChange={(e) => setFile(e.target.files[0])}
              className="file-input"
            />
            <button 
              onClick={handleFileUpload} 
              disabled={loading || !file}
              className="upload-btn"
            >
              {loading ? 'ÏóÖÎ°úÎìú Ï§ë...' : 'ÏóÖÎ°úÎìú'}
            </button>
          </div>
          {uploadStatus && (
            <div className="upload-status">
              {uploadStatus}
            </div>
          )}
        </section>

        {/* ÏßàÎ¨∏ ÏÑπÏÖò */}
        <section className="question-section">
          <h3>‚ùì ÏßàÎ¨∏ÌïòÍ∏∞</h3>
          <div className="question-container">
            <textarea
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Î¨∏ÏÑúÏóê ÎåÄÌï¥ ÏßàÎ¨∏Ìï¥Î≥¥ÏÑ∏Ïöî..."
              className="question-input"
              rows="3"
            />
            <button 
              onClick={handleQuestion} 
              disabled={loading || !question.trim()}
              className="question-btn"
            >
              {loading ? 'ÎãµÎ≥Ä ÏÉùÏÑ± Ï§ë...' : 'ÏßàÎ¨∏ÌïòÍ∏∞'}
            </button>
          </div>
        </section>

        {/* ÎãµÎ≥Ä ÏÑπÏÖò */}
        {answer && (
          <section className="answer-section">
            <h3>üí° ÎãµÎ≥Ä</h3>
            <div className="answer-container">
              <pre className="answer-text">{answer}</pre>
            </div>
          </section>
        )}

        {/* Í¥ÄÎ¶¨ ÏÑπÏÖò */}
        <section className="admin-section">
          <h3>‚öôÔ∏è Í¥ÄÎ¶¨</h3>
          <button onClick={handleClearDB} className="clear-btn">
            Î≤°ÌÑ∞ DB Ï¥àÍ∏∞Ìôî
          </button>
        </section>
      </main>
    </div>
  );
}

export default App;
